import React from 'react';
import initialExpensesData from './data.js'

import NewExpense from './components/NewExpense/NewExpense';
import Expenses from './components/Expenses/Expenses';


const App = () => {

  localStorage.setItem("expenses", JSON.stringify(initialExpensesData));


  const [expensesInLocalStorage, setExpensesInLocalStorage] = React.useState(
    localStorage.getItem("expenses")
  );

  console.log(initialExpensesData)
  console.log(JSON.parse(expensesInLocalStorage))


  const addExpenseHandler = expense => {

    console.log(initialExpensesData)
    console.log(JSON.parse(expensesInLocalStorage))
    let prevExpArr = localStorage.getItem("expenses");
    let newExpArr = [expense, ...expensesInLocalStorage]

    console.log(prevExpArr)
    console.log(newExpArr)

    setExpensesInLocalStorage(JSON.stringify(newExpArr))




    console.log('In App.js');
    console.log(expense);
    console.log(expensesInLocalStorage);
  };


  return (
    <div>
      <NewExpense onAddExpense={addExpenseHandler} />
      <Expenses items={JSON.parse(expensesInLocalStorage)} />
    </div>
  );
}

export default App;
